  nOfBits = 1000000;  Bits = randi([0,1], nOfBits, 1);  y=zeros(31);  z=zeros(31);  g = 0;  for EbNo = 0:0.5:15    EbNo    [y(g+1), z(g+1)] = CSK_Tester(Bits, EbNo);    g = g + 1;  end  EbNo=0:1:15;  %y(y == 0) = NaN  plot(EbNo, y, 'b')  ylabel('Bit Errors');  xlabel('Eb/No');  title('Bit errors caused by central symbol');  hold on  plot(EbNo, z, 'r')        