  nOfBits = 100000000;  Bits = randi([0,1], nOfBits, 1);  Bits_2 = Bits(1:10000000);  y=zeros(0);  z=zeros(0);  parfor EbNo = 0:1:15    EbNo    if EbNo < 12        y=[y, CSK_Tester(Bits_2, EbNo)];    else        y=[y, CSK_Tester(Bits, EbNo)];    end  end  EbNo=0:1:15;  %y(y == 0) = NaN  semilogy(EbNo, y, 'b')  ylabel('BER');  xlabel('Eb/No');  title('Bit Error as a function of SNR');  hold on        