  nOfBits_long = 99999983;  nOfBits_short = 9999985;  Bits_long = randi([0,1], 1, nOfBits_long);  Bits_short = Bits_long(1:nOfBits_short);  x=zeros(0);  parfor EbNo = 0:1:15    EbNo    y=EbNo;    if EbNo < 9         x=[x; CSK_Tester(Bits_short, y)];    else        x=[x; CSK_Tester(Bits_long, y)];    end  end  EbNo=0:1:15;  semilogy(EbNo, x,'Color','m');  ylabel('BER');  xlabel('Eb/No');  title('Bit Error as a function of SNR');  hold on